changeSet.sql:
UPDATE hiv_art_clinical
SET pregnancy_status = 'PREGNANCY_STATUS_PREGNANT'
WHERE pregnancy_status = 'Pregnant' OR pregnancy_status = 'pregnant';

UPDATE hiv_art_clinical
SET pregnancy_status = 'PREGNANCY_STATUS_BREASTFEEDING'
WHERE pregnancy_status ILIKE '%Breastfeeding%';

UPDATE hiv_art_clinical
SET pregnancy_status = 'PREGNANCY_STATUS_NOT_PREGNANT'
WHERE pregnancy_status IN ('Not Pregnant', 'Not pregnant');

UPDATE hiv_art_clinical
SET pregnancy_status = 'PREGNANCY_STATUS_POST_PARTUM'
WHERE pregnancy_status = 'Post Partum';
